mapper.select:
select

  distinct


from app_information

  


  order by ${orderByClause}


  
    limit ${offset}, ${limit}
  
  
    limit ${limit}

SELECT b.*, c.id cid, c.channel_id, c.app_id, c.prority, d.channel_name FROM ( SELECT a.id, a.app_name, a.app_spell, a.package_name, a.dev_id, a.dev_name, a.top_category, a.category, a. STATUS, a.description, a.version, a.versioncode, a.icon, a.icon_type, a.images, a.download, a.size, a.updated, a.created, a.remarks, a.label, a.star, a.orientation, a.md5, a.count FROM ( SELECT i.*, count(d.id) count FROM app_information i LEFT JOIN app_download d ON d.app_id = i.id GROUP BY i.id ) a
) b LEFT JOIN app_channel_list c ON c.app_id = b.id left join app_channel d on d.id=c.channel_id
order by ${example.orderByClause}
